import { useState } from "react";
import "./Header.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faBed } from "@fortawesome/free-solid-svg-icons";
import {
  faCalendarDays,
  faPaperPlane,
} from "@fortawesome/free-regular-svg-icons";
import { DateRange } from "react-date-range";
import "react-date-range/dist/styles.css"; // main style file
import "react-date-range/dist/theme/default.css"; // theme css file
import { format } from "date-fns";

const Header = () => {
  const [openDate, setOpenDate] = useState(false);
  const [date, setDate] = useState([
    {
      startDate: new Date(),
      endDate: new Date(),
      key: "selection",
    },
  ]);

const [openOption,setOpenOption]=useState(false);
const [option,setOption]=useState({
    adult:1,
    children:0,
    room:1,
})
const handleOption=(name,operation)=>{
    set
}

  return (
    <div className="header">
      <div className="headerContainer">
        <h1 className="headerTitle">A lifetime of discounts? It's Genius.</h1>
        <p className="headerDesc">
          Get rewarded for your travels â€“ unlock instant savings of 10% or more
          with a free Lamabooking account
        </p>
        <button className="headerBtn">Sign in / Register</button>
        <div className="headerSearch">
          <div className="headerSearchItem">
            <FontAwesomeIcon icon={faBed} className="headerIcon" />
            <input
              type="text"
              placeholder="Where are you going?"
              className="headerSearchInput"
            ></input>
          </div>
          <div className="headerSearchItem">
            <FontAwesomeIcon icon={faCalendarDays} className="headerIcon" />
            <span
              onClick={() => setOpenDate(!openDate)}
              className="headerSearchText"
            >{`${format(date[0].startDate, "MM,dd,yyyy")} to ${format(
              date[0].endDate,
              "MM,dd,yyyy"
            )}`}</span>
            {openDate && (
              <DateRange
                editableDateInputs={true}
                onChange={(item) => setDate([item.selection])}
                moveRangeOnFirstSelection={false}
                ranges={date}
                className="date"
              />
            )}
          </div>
          <div className="headerSearchItem">
            <FontAwesomeIcon icon={faPaperPlane} className="headerIcon" />
            <span className="headerSearchText">{`${option.adult} adult ${option.children} children ${option.room} room`}</span>
            <div className="options">
                <div className="optionItem">
                    <span className="openText">Adult</span>
                    <div className="optionCounter">
                    <button className="openCounterButton" onClick={()=>handleOption('adult','d')}>-</button>
                    <span className="optionCounterNumber">1</span>
                    <button className="openCounterButton" onClick={()=>handleOption('adult','i')}>+</button>
                    </div>
                </div>
                <div className="optionItem">
                    <span className="openText">Children</span>
                    <div className="optionCounter">
                    <button className="openCounterButton" onClick={()=>handleOption('children','d')}>-</button>
                    <span className="optionCounterNumber">0</span>
                    <button className="openCounterButton" onClick={()=>handleOption('children','i')}>+</button >
                    </div>
                </div>
                <div className="optionItem">
                    <span className="openText">Room</span>
                    <div className="optionCounter">
                    <button className="openCounterButton">-</button>
                    <span className="optionCounterNumber">1</span>
                    <button className="openCounterButton">+</button>
                    </div>
                </div>
            </div>
          </div>
          <div className="headerSearchItem">
            <button className="headerBtn">Search</button>
          </div>
        </div>
      </div>
    </div>
  );
};
export default Header;
