import React, { useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faCircleXmark } from "@fortawesome/free-regular-svg-icons";
import { faLocationDot } from "@fortawesome/free-solid-svg-icons";

const HotelDetail = () => {
  const [slideNumber, setSlideNumber] = useState(0);
  const [open, setOpen] = useState(false);
  const photos = [
   {src:"./images/hotel_detail_1.jpg"},
    {src:"./images/hotel_detail_2.jpg"},
    {src:"./images/hotel_detail_3.jpg"},
    {src:"./images/hotel_detail_4.jpg"},
    {src:"./images/hotel_detail_5.jpg"},
    {src:"./images/hotel_detail_6.jpg"},
  ];
  const handleOpen = (i) => {
    setSlideNumber(i);
    setOpen(true);
  };

  return (
    <div className="hotelContainer">
      <div className="hotelWrapper">
        <h1>Tower Street Apartments</h1>
        <div className="hotelAddress">
          <FontAwesomeIcon icon={faLocationDot} />
          <span>Elton St 125 New york</span>
        </div>
        <span className="hotelDistance">
          Excellent location â€“ 500m from center
        </span>
        <span className="hotelPriceHighlight">
          Book a stay over $114 at this property and get a free airport taxi
        </span>
        <div className="hotelImages">
          {photos.map((photo, i) => (
            <div className="hotelImgWrapper" key={i}>
              <img
                onClick={() => handleOpen(i)}
                src={photo.src}
                alt=""
                className="hotelImg"
              />
            </div>
          ))}
        </div>
        <div className="hotelDetails">
            <div className="hotelDetailsTexts">
            <h1 className="hotelTitle">Stay in the heart of City</h1>
            
            </div>
        </div>
      </div>
    </div>
  );
};

export default HotelDetail;
